package com.baesh.careersummary.controller;import com.baesh.careersummary.dto.PortfolioRequest;import com.baesh.careersummary.dto.SummaryResponse;import com.baesh.careersummary.service.SummaryService;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/api/summary")public class SummaryController {    private final SummaryService summaryService;    public SummaryController(SummaryService summaryService) {        this.summaryService = summaryService;    }    @PostMapping    public SummaryResponse summarize(@RequestBody PortfolioRequest request) {        return summaryService.summarizePortfolio(request);    }}