package com.baesh.careersummary.util;import org.springframework.http.*;import org.springframework.stereotype.Component;import org.springframework.web.client.RestTemplate;import java.util.HashMap;import java.util.Map;@Componentpublic class LocalLLMClient {    private final RestTemplate restTemplate = new RestTemplate();    public String requestSummary(String prompt) {        String url = "http://localhost:8000/summarize";        HttpHeaders headers = new HttpHeaders();        headers.setContentType(MediaType.APPLICATION_JSON);        Map<String, String> payload = new HashMap<>();        payload.put("prompt", prompt);        payload.put("format", "JSON");        HttpEntity<Map<String, String>> request = new HttpEntity<>(payload, headers);        ResponseEntity<String> response = restTemplate.postForEntity(url, request, String.class);        return response.getBody();    }}